$(document).ready(function(){$('.evaluate-form').on('submit',function(){let data=new FormData();data.append("mt_file",document.querySelector("#mt_file").files[0])
data.append("ht_file",document.querySelector("#ht_file").files[0])
$('.evaluate-status').attr('data-status','pending');$.ajax({url:$(this).attr("action"),method:'POST',data:data,contentType:false,cache:false,processData:false,success:function(evaluation){for(metric of evaluation.metrics){let template=document.importNode(document.querySelector("#metric-template").content,true);let[min,value,max]=metric.value;let proportion=max-min;value=(100*value)/proportion;$(template).find(".metric-name").html(metric.name);$(template).find(".metric-value").html(value);$(template).find(".metric-indicator").css({"left":`${value*100}%`})
$(".evaluate-results-row").append(template);}
let bpl_table=$(".bleu-line").DataTable();$('.evaluate-status').attr('data-status','none');$(".evaluate-results").removeClass("d-none");}})
return false;});})
